import React, { useState } from "react";
import {
  MDBMask,
  MDBRow,
  MDBCol,
  MDBBtn,
  MDBView,
  MDBContainer,
  MDBForm,
  MDBAnimation,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBJumbotron,
} from "mdbreact";
import "./index.css";
import API from "../../utils/API";
import ResultCard from "../Search-Result-Card";

function SearchPage() {
  const [searchName, setSearchName] = useState({
    name: "",
    errormessage: "",
  });
  console.log(("searchName: ", searchName));

  const [results, setResults] = useState({
    results: [],
    characters: [],
  });
  console.log("results: ", results);

  // const [charactersObj, setCharactersObj] = useState({});
  // console.log("characters: ", charactersObj);

  function handleInputChange(event) {
    event.preventDefault();
    setSearchName({
      name: event.target.value,
    });
  }

  function handleFormSubmit(event) {
    event.preventDefault();
    API.getSuperhero(searchName)
      .then((res) => {
        console.log("res: ", res);
        let character = res.data.results.map((character) => {
          return {
            img: character.image.url,
            name: character.name,
            alignment: character.biography.alignment,

            combat: character.powerstats.combat,
            durability: character.powerstats.durability,
            intelligence: character.powerstats.intelligence,
            power: character.powerstats.power,
            speed: character.powerstats.speed,
            strength: character.powerstats.strength,

            work: character.work.occupation,
          };
        });
        console.log("character: ", character);
        setResults({
          results: res.data.results,
          characters: character,
        });
        // setCharactersObj({
        //   ...charactersObj,
        //   character: res.data.results.map((character) => {
        //     return
        //     {
        //       img: character.image.url,
        //       name: character.name,
        //       alignment: character.biography.alignment,
        //       stats: character.powerstats,
        //       work: character.work.occupation,
        //     };
        //   }),
        // });
        // setCharacters({ character });
        // console.log("characters.length: ", charactersObj.length);
      })
      .catch(console.error);
  }

  return (
    <div>
      <div id="apppage">
        <MDBView>
          <MDBMask className="d-flex justify-content-center align-items-center gradient">
            <MDBContainer>
              <MDBRow>
                <MDBCol
                  md="8"
                  className="white-text text-center text-md-left mt-xl-5 mb-5"
                >
                  <MDBAnimation type="fadeInLeft" delay=".3s">
                    <h1 className="h1-responsive font-weight-bold mt-sm-5">
                      Search over 700 comicbook superheroes and villains!
                    </h1>
                    <hr className="hr-light" />
                    <div className="active-pink-3 active-pink-4 mb-4">
                      <input
                        onChange={handleInputChange}
                        className="form-control"
                        type="text"
                        placeholder="Search"
                        aria-label="Search"
                      />
                      <MDBBtn
                        onClick={handleFormSubmit}
                        color="secondary"
                        className="ml-0"
                        size="sm"
                      >
                        Search
                      </MDBBtn>
                    </div>
                  </MDBAnimation>
                </MDBCol>

                {/* <MDBCol md="6" xl="5" className="mt-xl-5">
                  <MDBAnimation type="fadeInRight" delay=".3s">
                    <img
                      src={this.props.img}
                      alt=""
                      className="img-fluid rounded rightCardImg"
                    />
                  </MDBAnimation>
                </MDBCol> */}
              </MDBRow>
            </MDBContainer>
          </MDBMask>
        </MDBView>
      </div>
      <MDBContainer
        fluid
        className="justify-content-center align-items-center flex"
      >
        {/* <MDBAnimation type="fadeInDownBig" delay=".3s"> */}
        <ResultCard characters={results.characters} />
        {/* </MDBAnimation> */}
      </MDBContainer>
    </div>
  );
}

export default SearchPage;
